import React from 'react';
import { useDispatch } from 'react-redux';
import './Square.css';

export function Square(props) {

    const color = props.color;
    let border = 'border';
    if (color === 'white') {
        color = 'black';
    } else if (color === 'black') {
        color = 'white';
    }

    const dispatch = useDispatch();

    return (<div onClick = {() => dispatch(
        {
        type: 'boardClick',
        x: props.x,
        y: props.y,
        }
    )} id = "square" class = {color}>
        {symbol}
    </div>);
}
