const defaultState = [
    ['',''],
    ['','']
];

export default function gameReducer(
    state = defaultState, action
) {
    if (action.type === 'boardClick') {
        const value = state[action.x][action.y];
        if (value === 'white') {
            state[action.x][action.y] = 'black';
            color = 'black';
        } else {
            state[action.x][action.y] = 'white';
            color = 'white';
        }
        return [...state];
    }
    return state;
}
